import { Box, Card, CardContent, Grid, Typography } from '@mui/material';
import vegetables from '../assets/vegetables.svg'



export const HomeScreen = () => {

  const Item = (title: any, content: any) => {
    return (
      <Grid item xs={12} sm={6} sx={{
        display: 'flex',
        flexDirection: 'column',
      }}>
        <Typography>{title}</Typography>
        <Card sx={{ height: '100%' }}>
          <CardContent>{content}</CardContent>
        </Card>
      </Grid>
    );
  };

  return (
    <Grid
      sx={{p: 5}}
      container
      spacing={3}
      justifyContent="space-between"
      alignItems="stretch"
    >
      <Item
        content="Lots and lots and lots and lots and lots and lots of content!"
        title="Big Title 1"
      />
      <Item
        title="Big Title 2"
        content="Not much content"
      />
    </Grid>
  )
}
