import { createTheme } from '@mui/material';
import { grey, red } from '@mui/material/colors';

export const lightTheme = createTheme({
  palette: {
    mode: 'light',
    background: {
      default: grey[300]
    },
    primary: {
      main: '#4a148c',
      dark: '#ffffff',
      light: '#000000'
    },
    secondary: {
      main: '#90bf3b'
    },
    // jj: {
    //   main: '#90bf3b'
    // },
    error: {
      main: red.A400
    }
  },

  components: {
    MuiAppBar: {
      defaultProps: {elevation: 0},
      styleOverrides: {}
    },
    MuiButton: {
      defaultProps: {color: 'error'},
      // styleOverrides: {colorInherit: 'red'}
    }
  }
});
