import { Box, Card, CardContent, Grid, Typography } from '@mui/material';
import vegetables from '../assets/vegetables.svg'

export const HomeScreen = () => {

  const Item = (title: string, content: string, ...rest: any) => {
    return (
      <Grid item {...rest} sx={{
        display: 'flex',
        flexDirection: 'column',
      }}>
        <Typography>{title}</Typography>
        <Card sx={{ height: '100%' }}>
          <CardContent>{content}</CardContent>
        </Card>
      </Grid>
    );
  };

  return (
    <Grid
      sx={{p: 5}}
      container
      spacing={3}
      justify="space-between"
      alignItems="stretch"
    >
      <Item
        xl={2}
        lg={2}
        md={6}
        xs={12}
        content="Lots and lots and lots and lots and lots and lots of content!"
        title="Big Title 1"
      />
      <Item
        xl={4}
        lg={4}
        md={6}
        xs={12}
        title="Big Title 2"
        content="Not much content"
      />
    </Grid>
  )
}
